import unittest
from pathlib import Path

from PIL import Image

from app.controllers.thumbnail import ThumbnailImage
from app.models.picture import PictureOrientation

CANON_EOS_70D_THUMBNAIL = ""  # noqa: E501
VERTICAL_IMAGE_THUMBNAIL = ""  # noqa: E501
HORIZONTAL_IMAGE_THUMBNAIL = ""  # noqa: E501

CANON_EOS_70D_IMAGE_NAME = "tests/files/test-canon-eos70D.jpg"
VERTICAL_IMAGE_NAME = "tests/files/test_image.JPG"
HORIZONTAL_IMAGE_NAME = "tests/files/IMG_7095-300px.JPG"


class TestThumbnailImage(unittest.IsolatedAsyncioTestCase):
    def setUp(self) -> None:
        self.canon_eos_70_picture = Image.open(Path(CANON_EOS_70D_IMAGE_NAME))
        self.vertical_image = Image.open(Path(VERTICAL_IMAGE_NAME))
        self.horizontal_image = Image.open(Path(HORIZONTAL_IMAGE_NAME))

    async def test_get_base64_thumbnail(self):
        image = ThumbnailImage(self.canon_eos_70_picture, thumbnail_size=5)
        base64_thumbnail = await image.get_base64_thumbnail()

        self.assertEqual(CANON_EOS_70D_THUMBNAIL, base64_thumbnail)

    async def test_get_base64_thumbnail_vertical(self):
        image = ThumbnailImage(self.vertical_image, thumbnail_size=10)
        base64_thumbnail = await image.get_base64_thumbnail()

        self.assertEqual(VERTICAL_IMAGE_THUMBNAIL, base64_thumbnail)

    async def test_get_base64_thumbnail_horizontal(self):
        image = ThumbnailImage(self.horizontal_image, thumbnail_size=10)
        base64_thumbnail = await image.get_base64_thumbnail()

        self.assertEqual(HORIZONTAL_IMAGE_THUMBNAIL, base64_thumbnail)

    async def test_get_base64_original_image_unchanged(self):
        start_size = self.vertical_image.size

        image = ThumbnailImage(self.vertical_image)
        await image.get_base64_thumbnail()

        self.assertEqual(start_size, self.vertical_image.size)

    async def test_get_orientation_vertical(self):
        image = ThumbnailImage(self.vertical_image)

        self.assertEqual(PictureOrientation.PORTRAIT, await image.get_orientation())

    async def test_get_orientation_horizontal(self):
        image = ThumbnailImage(self.horizontal_image, thumbnail_size=5)

        self.assertEqual(PictureOrientation.LANDSCAPE, await image.get_orientation())
